
$(document).ready(function() {
  
  //Variables
  var wins = 0;
  var losses = 0;
  var totalScore;
  
  //random number 19-120
  var random;
  
  
  var btn1;
  var btn2;
  var btn3;
  var btn4;
  
  
  
  //random number generated by the computer between 19-120
  var computerNumber = function() {
        
        document.querySelector("#random-number").innerHTML = random;
      };  
   
  //wins and losses set to zero at start
  var winsCounter = function() {
        
        document.querySelector("#winsDisp").innerHTML = wins;
      };  
  
  var lossesCounter = function() {
        
        document.querySelector("#lossesDisp").innerHTML = losses;
      };  
  
  var scoreCounter = function() {
  
    document.querySelector("#totalScoreBox").innerHTML = totalScore;
  }
  
  //reset game
  var reset = function() {
    totalScore = 0;
     random = Math.floor((Math.random()* 102) + 19);//random number 19-120
     btn1 = Math.floor((Math.random() * 12) + 1);//random number 1-12
     btn2 = Math.floor((Math.random() * 12) + 1);
     btn3 = Math.floor((Math.random() * 12) + 1);
     btn4 = Math.floor((Math.random() * 12) + 1);
      computerNumber();
      winsCounter();
      lossesCounter();
      scoreCounter();
  }
  reset()

  //click events
  $(".btn1").on("click", function() {

 //$(this).attr("btn1");d
      totalScore += btn1;
      scoreCounter();
      setTimeout(winOrLose, 10);

  // Again
        });
  $(".btn2").on("click", function() {
      totalScore += btn2;
      scoreCounter();
      setTimeout(winOrLose, 10);  
        });
  $(".btn3").on("click", function() {
        totalScore += btn3;
      scoreCounter(); 
      setTimeout(winOrLose, 10); 
        });
  $(".btn4").on("click", function() {
       totalScore += btn4;
      scoreCounter(); 
      setTimeout(winOrLose, 10); 
        });

  //score display
  var winOrLose = function() {
  
  if (totalScore === random) {
    wins += 1;
    alert("You win!");
    reset();
  
  }
  
  else if (totalScore > random) {
    losses += 1;
    alert("You lost!");
    reset();
  }
  
}	
  })
  